import {
  Equation,
  EquationEvaluate,
  EquationOptions,
  defaultErrorHandler,
} from "react-equation";
import { defaultVariables, defaultFunctions } from "equation-resolver";
import {
  Form,
  Button,
  Container,
  Row,
  Col,
  Navbar,
  Nav,
} from "react-bootstrap";
import React, { useEffect, useState } from "react";

import "./styles.css";
export default function HomeScreen() {
  const [equation, setEquation] = useState("f(x)=");
  const [xi, setXi] = useState(0);
  const [xu, setXu] = useState(0);
  const [stop, setStop] = useState(0);
  const [iteration, setIteration] = useState(0);

  useEffect(() => {}, []);
  return (
    <>
      <Navbar className="navbar-dark bg-primary" bg="primary" expand="lg">
        <div className="container-fluid">
          <Navbar.Brand href="#home">Bisection Method</Navbar.Brand>
        </div>
      </Navbar>
      <Container>
        <main>
          <Row
            className="justify-content-md-center"
            style={{ width: "100%", margin: "0" }}
          >
            <Col xs={12} md={6}>
              <h3>Bisection Method</h3>

              <Form>
                <Form.Group controlId="equation" className="mb-4">
                  <Form.Label>Inserta la ecuación</Form.Label>
                  <Form.Control
                    className="mb-4"
                    type="text"
                    placeholder="La ecuación"
                    value={!equation.startsWith("f(x)=") ? "f(x)=" : equation}
                    onChange={(e) => {
                      setEquation(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                  <div>
                    <Equation
                      value={`${
                        !equation.startsWith("f(x)=") ? "f(x)=" : equation
                      }`}
                      errorHandler={defaultErrorHandler}
                    />
                  </div>
                </Form.Group>
                <Form.Group controlId="xi">
                  <Form.Label>Inserta el valor de xi</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={xi}
                    onChange={(e) => {
                      setXi(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <Form.Group controlId="xu">
                  <Form.Label>Inserta el valor de xu</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={xu}
                    onChange={(e) => {
                      setXu(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <Form.Group controlId="stop">
                  <Form.Label>Inserta el criterio de parada</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={xu}
                    onChange={(e) => {
                      setXu(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <Form.Group controlId="iteration">
                  <Form.Label>Inserta el valor de xu</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={xu}
                    onChange={(e) => {
                      setXu(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <Button type="submit" variant="primary">
                  Calcular
                </Button>
              </Form>
            </Col>
          </Row>
        </main>
      </Container>
    </>
  );
}
