import {
  Equation,
  EquationEvaluate,
  EquationOptions,
  defaultErrorHandler,
} from "react-equation";
import { defaultVariables, defaultFunctions } from "equation-resolver";
import {
  Form,
  Button,
  Container,
  Row,
  Col,
  Navbar,
  Nav,
  Table,
} from "react-bootstrap";
import React, { useEffect, useState } from "react";
import Loader from "../../components/Loader";
import Message from "../../components/Message";
import "./styles.css";
import axios from "axios";

export default function HomeScreen() {
  const [equation, setEquation] = useState("f(x)=");
  const [xi, setXi] = useState(0);
  const [xu, setXu] = useState(0);
  const [stop, setStop] = useState(0);
  const [iteration, setIteration] = useState(0);
  const [loading, setIsLoading] = useState(false);
  const [results, setResults] = useState();
  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };
      const { data } = await axios.post(
        "/solve/",
        {
          equation: equation.replace("f(x)=", ""),
          xi: xi,
          xu: xu,
          stop: stop,
        },
        config
      );
      setIsLoading(false);
      setResults(data);
      console.log(data);
    } catch (error) {
      console.log(error);
      setIsLoading(false);
    }
  };
  useEffect(() => {}, []);
  return (
    <>
      <Navbar className="navbar-dark bg-primary" bg="primary" expand="lg">
        <div className="container-fluid">
          <Navbar.Brand href="#home">Bisection Method</Navbar.Brand>
        </div>
      </Navbar>
      <Container style={{ marginTop: "10vh" }}>
        <main>
          <Row
            className="justify-content-md-center"
            style={{ width: "100%", margin: "0" }}
          >
            <Col xs={12} md={6}>
              <h3>Bisection Method</h3>

              <Form onSubmit={submitHandler}>
                <Form.Group controlId="equation" className="mb-4">
                  <Form.Label>Inserta la ecuación</Form.Label>
                  <Form.Control
                    className="mb-4"
                    type="text"
                    placeholder="La ecuación"
                    value={!equation.startsWith("f(x)=") ? "f(x)=" : equation}
                    onChange={(e) => {
                      setEquation(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                  <div>
                    <Equation
                      value={`${
                        !equation.startsWith("f(x)=") ? "f(x)=" : equation
                      }`}
                      errorHandler={defaultErrorHandler}
                    />
                  </div>
                </Form.Group>
                <Form.Group controlId="xi">
                  <Form.Label>Inserta el valor de xi</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={xi}
                    onChange={(e) => {
                      setXi(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <Form.Group controlId="xu">
                  <Form.Label>Inserta el valor de xu</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={xu}
                    onChange={(e) => {
                      setXu(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <Form.Group controlId="stop">
                  <Form.Label>Inserta el criterio de parada</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="xi"
                    value={stop}
                    onChange={(e) => {
                      setStop(e.target.value);
                    }}
                    required
                  ></Form.Control>{" "}
                </Form.Group>
                <div className="mb-4"></div>
                <Button type="submit" variant="primary">
                  Calcular
                </Button>
              </Form>
            </Col>
          </Row>
        </main>
        <Table striped bordered hover responsive className="table-sm mt-3">
          <thead>
            <tr>
              <th>Iteración</th>
              <th>xi</th>
              <th>xu</th>
              <th>f(xi)</th>
              <th>f(xr)</th>
              <th>Ea</th>
              <th>Signo</th>
            </tr>
          </thead>
          <tbody>
            {results?.map((result) => (
              <tr key={result[0]}>
                
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    </>
  );
}
