import {
  Equation,
  EquationEvaluate,
  EquationOptions,
  defaultErrorHandler,
} from "react-equation";
import { defaultVariables, defaultFunctions } from "equation-resolver";
import { Form } from "react-bootstrap";
import React, { useEffect, useState } from "react";

export default function HomeScreen() {
  const [equation, setEquation] = useState("f(x)=");

  useEffect(() => {}, []);
  return (
    <>
      <h2>Hola como estas verga</h2>
      <div>
        <Equation value={`${equation != ""?equation : "f(x)="}`} />
      </div>    
      <Form.Group controlId="equation">
        <Form.Label>Ecuación</Form.Label>
        <Form.Control
          type="text"
          placeholder="La ecuación"
          value={equation != ""?equation : "f(x)="}
          onChange={(e) => setEquation(e.target.value)}
          required
        ></Form.Control>{" "}
      </Form.Group>
    </>
  );
}
